import { Component } from '@angular/core';
import {GreetingServiceService} from "../service/greeting-service.service";
import {Greeting} from "../entity/Greeting";
import {AuthenticateService} from "../service/authenticate.service";
import {Credentials} from "../entity/Credentials";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'demo-angular';

  constructor(private greetingService: GreetingServiceService,
    private authService: AuthenticateService) {
    // this.login();
    let i=0;
    for (i=0; i<100; i++) {
      this.getGreeting().subscribe((data: Greeting) => {
        console.log('Greeting:[', i, '] [', data, '].');
      });
    }
  }

  login() {
    const cridentials:Credentials = {username: 'itm94lj',
      password: 'password'};
    this.authService.authenticate(cridentials, () => {
      console.log('login success.');
      let i=0;
      for (i=0; i<100; i++)
      {
        this.getGreeting();
      }

      this.getGreeting();

    })
  }



  getGreeting() {
    return this.greetingService.getGreeting();
  }
}


