import { Component } from '@angular/core';
import {GreetingServiceService} from "../service/greeting-service.service";
import {OAuthService} from "angular-oauth2-oidc/index";
import {authCodeFlowConfig} from "../config/auth-config";
import {Router} from "@angular/router";
import {filter} from "rxjs/dist/types/internal/operators/filter";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'demo-angular';

  constructor(private greetingService: GreetingServiceService,
    private oauthService: OAuthService,
    private router: Router) {
    this.configureCodeFlow();

    this.oauthService.events
      .pipe(filter((e) => e.type === 'token_received'))
      .subscribe((_) => {
        console.log('state:[', this.oauthService.state, '].');
        this.oauthService.loadUserProfile();
        const scopes = this.oauthService.getGrantedScopes();
        console.log('scopes:[', scopes, '].');
      })
  }


  private configureCodeFlow() {
    this.oauthService.configure(authCodeFlowConfig);
    this.oauthService.loadDiscoveryDocumentAndTryLogin()
      .then((_) => {
        console.log("login success.");
      });

    this.oauthService.setupAutomaticSilentRefresh();
  }




  getGreeting() {
    return this.greetingService.getGreeting();
  }
}


