import { Component } from '@angular/core';
import {GreetingServiceService} from "../service/greeting-service.service";
import {Router} from "@angular/router";
import {OAuthService, OAuthEvent} from "angular-oauth2-oidc";
import {authCodeFlowConfig} from "../config/auth-config";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'demo-angular';
  loginFailed: boolean = false;
  userProfile: any;

  constructor(private greetingService: GreetingServiceService,
              private oauthService: OAuthService,
    private router: Router) {
    this.configureCodeFlow();
  }



  private configureCodeFlow() {
    this.oauthService.configure(authCodeFlowConfig);
    this.oauthService.loadDiscoveryDocument();

    this.oauthService.events
      .subscribe((e: OAuthEvent) => {
        console.log('event received:[', e.type,']');
          if (e.type === 'token_received') {
            console.log('token_received');
            this.oauthService.loadUserProfile();
          }
      }
        );

    this.oauthService.setupAutomaticSilentRefresh();
  }

  loadUserProfile(): void {
    this.oauthService.loadUserProfile()
      .then();
  }

  get access_token() {
    return this.oauthService.getAccessToken();
  }

  get access_token_expiration() {
    return this.oauthService.getAccessTokenExpiration();
  }

  get givenName() {
    var claims = this.oauthService.getIdentityClaims();
    if (!claims) return null;
    return claims['given_name'];
  }

  get familyName() {
    var claims = this.oauthService.getIdentityClaims();
    if (!claims) return null;
    return claims['family_name'];
  }

  loginWithPassword() {
    console.log('login processed.');
    this.oauthService.loadDiscoveryDocumentAndLogin();
  }

  logout() {
    this.oauthService.logOut(true);
  }
}


<h1 *ngIf="!givenName">Welcome!</h1>
<h1 *ngIf="givenName">Welcome, {{ givenName }} {{ familyName }}!</h1>

<div class="panel panel-default">
  <div class="panel-body">
    <p>Login with Username/Password</p>

    <p style="color:red; font-weight:bold" *ngIf="loginFailed">
      Login wasn't successfull.
    </p>

    <div class="form-group">
      <label>Username</label>
    </div>
    <div class="form-group">
      <label>Password</label>
    </div>
    <div class="form-group">
      <button class="btn btn-default" (click)="loginWithPassword()">
        Login
      </button>
      <button class="btn btn-default" (click)="logout()">Logout</button>
    </div>
  </div>
</div>

<div class="panel panel-default">
  <div class="panel-body"><b>Username/Password:</b> max/geheim</div>
</div>

<div class="panel panel-default">
  <div class="panel-body">
    <p><b>access_token_expiration:</b> {{ access_token_expiration }}</p>
  </div>
</div>

<div class="panel panel-default">
  <div class="panel-body">
    <p><b>access_token:</b> {{ access_token }}</p>
    <div *ngIf="userProfile">
      <b>user profile:</b>
      <pre>{{ userProfile | json }}</pre>
    </div>
  </div>
</div>
<router-outlet></router-outlet>
